        return (
            <tr>
                <td className="product_remove"><i className="fa fa-trash-o" /></td>
                <td className="product_thumb">
                <a href="product-details-default.html">
                <img src={item.product.image1} alt="" /></a></td>
                <td className="product_name"><a href="product-details-default.html">{item.product.name}</a></td>
                <td className="product-price">${item.product.price}</td>
                <td className="product_quantity"><label>Quantity</label> <input min={1} max={30} defaultValue={item.quantity} type="number" /></td>
                <td className="product_total">${this.showTotal(item.product.price * item.quantity )}</td>
            </tr>
        );
    }
    showTotal = (quantity, price) => {
        return quantity * price
    }
}

export default CartItem;
