import * as types from '../actions/index'

var data = JSON.parse(localStorage.setItem("CART"))

var initialState = data ? data : []

const cart = (state = initialState, action) => {
    switch(action.type){
        case types.atcAddToCart:
            console.log(action);
            return [...state]
        default : return [...state]
    }
}
export default cart
