var initicalState = [
    {
        product : {
                id:uuidv4(),
                name: "iPhone XS max",
                image1:"https://cdn.tgdd.vn/Products/Images/42/190322/iphone-xs-max-256gb-xam-1-1-org.jpg",
                image2: "https://cdn.tgdd.vn/Products/Images/42/190322/iphone-xs-max-256gb-mau-vang-dong-1-org.jpg",
                price:900,
                description:"",
                inventory:33,
                rating: 3
        },
        quantity : 5
    }
]

const cart = (state = initialState, action) => {
    switch(action.type){
        case types.atcAddToCart:
            console.log(action);
            return [...state]
        default : return [...state]
    }
}
export default cart
