import * as types from '../constans/index'

export const atcAddToCart = (product, quantity) =>{
    return {
        type : types.ADD_TO_CART,
        product,
        quantity
    }
}

export const atcChangeMessage = message => {
    return {
        type : types.CHANGE_MSG,
        message
    }
}
